export default {
  props: ['pageCreated'],
  computed: {
    isFormInvalid() {
      return !this.pageTitle || !this.pageContent || !this.linkText || !this.linkUrl;
    }
  },
  data() {
    return {
      pageTitle: '',
      pageContent: '',
      linkText: '',
      linkUrl: '',
      published: true
    };
  },
  methods: {
    submitForm() {
      if (!this.pageTitle || !this.pageContent || !this.linkText || !this.linkUrl) {
        alert('Please fill in all fields');
        return;
      }
      this.pageCreated({
        title: this.pageTitle,
        content: this.pageContent,
        link: {
          text: this.linkText,
          url: this.linkUrl
        },
        published: this.published
      });

      // Limpa os campos após o envio
      this.pageTitle = '';
      this.pageContent = '';
      this.linkText = '';
      this.linkUrl = '';
      this.published = true;
    },
    formatLinkUrl() {
      const textoSemEspacos = this.linkText.replace(/\s+/g, '');
      this.linkUrl = `${textoSemEspacos}.html`;
    }
  },
  watch: {
    pageTitle(newTitle, oldTitle) {
      if (this.linkText === oldTitle) {
        this.linkText = newTitle;
      }
    },
    linkText(newText) {
      this.formatLinkUrl();
    }
  }
};
